import { Component } from '@angular/core';
import { ItemService } from '../../services/item.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-item-create',
  templateUrl: './item-create.component.html',
  styleUrls: ['./item-create.component.css']
})
export class ItemCreateComponent {
  name = '';
  description = '';
  price = 0;

  constructor(private itemService: ItemService, private router: Router) { }

  createItem() {
    const item = { name: this.name, description: this.description, price: this.price };
    this.itemService.createItem(item).subscribe(() => {
      this.router.navigate(['/items']);
    });
  }
}
<h2>Create New Item</h2>
<form (ngSubmit)="createItem()">
  <div>
    <label for="name">Name:</label>
    <input type="text" id="name" [(ngModel)]="name" name="name" required />
  </div>
  <div>
    <label for="description">Description:</label>
    <textarea id="description" [(ngModel)]="description" name="description"></textarea>
  </div>
  <div>
    <label for="price">Price:</label>
    <input type="number" id="price" [(ngModel)]="price" name="price" required />
  </div>
  <button type="submit">Create</button>
</form>
<a routerLink="/items">Back to List</a>
